RKZ.Functions.GetVehiculeDirection = function()
    local _src = PlayerPedId()
    local _srcCoords = GetEntityCoords(_src)
    local _srcDirection = GetOffsetFromEntityInWorldCoords(_src, 0,10,0)
    local rayHandle = StartShapeTestRay(_srcCoords, _srcDirection, 10, _src, 0)
    local numRayHandle, hit, endCoords, surfaceNormal, entityHit = GetShapeTestResult(rayHandle)

    if hit == 1 and GetEntityType(entityHit) == 2 then
        return entityHit
    end
end

