RKZ.Functions.TriggerServerCallback = function(name, cb, ...)
    RKZ.ServerCallbacks[RKZ.CurrentRequestId] = cb

    TriggerServerEvent('RKZ-Base:server:triggerServerCallback', name, RKZ.CurrentRequestId, ...)

    if RKZ.CurrentRequestId < 65535 then
        RKZ.CurrentRequestId = RKZ.CurrentRequestId +1
    else
        RKZ.CurrentRequestId = 0
    end
end

RKZ.Functions.GetPlayers = function()
    local MaxPlayers = 120
    local players = {}

    for i=0, MaxPlayers, 1 do
        local _src = GetPlayerPedId(i)
        if DoesEntityExist(_src) then
            table.insert(players,i)
        end
    end
    return players
end

